mapper.select:
SELECT r.userno, r.status, r.roomno, r.roomtype, r.confirmdate, r.address, p.dealingtype, p.deposit, p.price FROM room r INNER JOIN price p ON r.roomno=p.roomno INNER JOIN user u ON r.userno=#{userno}
GROUP BY r.roomno ORDER BY r.roomno
LIMIT #{offset}, #{listCount}

mapper.update:
UPDATE room SET roomtype=#{roomtype}, title=#{title}, floor=#{floor},
	area=#{area}, fee=#{fee}, confirmdate=#{confirmdate}, address=#{address}, dong=#{dong}, ho=#{ho}, latitude=#{latitude}, 
	longitude=#{longitude}, region_2depth_name=#{region_2depth_name},region_3depth_name=#{region_3depth_name}, userno=#{userno}
WHERE roomno=#{roomno};

mapper.select:
SELECT COUNT(*) FROM room


		roomtype LIKE concat('%', #{roomtype}, '%')



		OR floor LIKE concat('%', #{floor}, '%')


		status = #{status}


		AND userno = #{userno}

SELECT r.roomno, r.roomtype, p.dealingtype, p.price, title, floor, area, fee, confirmdate, address, dong, ho, latitude, longitude, region_2depth_name, region_3depth_name, r.userno, u.name , r.status, count(f.roomno) AS cnt FROM room r INNER JOIN user u ON r.userno=u.userno INNER JOIN price p ON r.roomno=p.roomno LEFT OUTER JOIN (SELECT roomno, count(roomno) from fakeroom group by roomno)f ON r.roomno=f.roomno WHERE confirmdate is NOT NULL
GROUP BY roomno ORDER BY roomno ASC
LIMIT #{offset}, #{listCount}
